 /****************************************************************************
 [pf_config_ext.h] - PrFILE2 extended configuration file
 ****************************************************************************/
#ifndef __PF_CONFIG_EXT_H__
#define __PF_CONFIG_EXT_H__

/***************************************************************************
  CONFIGURATIONS
 ***************************************************************************/
#if USE_EBINDER
  /*
   * In eBinder environment, PrFILE2's system parameters are defined in
   * configuration file 'eb_prfile2.cfg', which is auto-generated by
   * eBinder Configurator.
   */
#if defined(__PRKERNEL_AMB__) || defined(_AMBA_KAL_)
#include <hardcfg.h>
#else
#include "prfile2/eb_pf_config_ext.h"
#endif

#else /* ! USE_EBINDER */

/*
 * If set, Unicode Interface is supported.
 * Default : 0
 */
#define PF_UNICODE_INTERFACE_SUPPORT                0

/*
 * If set, "PrFILE2 Free Cluster Search" is supported.
 * Default : 0
 */
#define PF_FREE_CLUSTER_SEARCH_SUPPORT              0

/*
   * If set, exFAT is supported.
   * Default : 0
   * NOTES:
   *  - This parameter determines that available use File System.
   *    0 : FAT12/FAT16/FAT32
   *    1 : FAT12/FAT16/FAT32/exFAT
   */
#define PF_EXFAT_SUPPORT                            (1)

#endif /* USE_EBINDER */

#endif /* __PF_CONFIG_EXT_H__ */
